import { useState } from "react"
import { useCookies } from 'react-cookie'

const Auth = () => {
  const [cookies, setCookie, removeCookie] = useCookies(null)
  const [isLogIn, setIsLogin] = useState(true)
  const [email, setEmail] = useState(null)
  const [password, setPassword] = useState(null)
  const [confirmPassword, setConfirmPassword] = useState(null)
  const [error, setError] = useState(null)

  const handleSubmit = async(e, endpoint) => {
    e.preventDefault()
    if (!isLogIn && password !== confirmPassword) {
      setError('Make sure passwords match!')
      return
    }

    const response = await fetch(`http://localhost:8000/${endpoint}`, {
      method: 'POST',
      headers: { 'Content-Type' : 'application/json' },
      body: JSON.stringify({ email, password })
    })

    const data = await response.json()
    
    if (data.detail) {
      setError(data.detail)
    } else {
      setCookie('Email', data.email)
      setCookie('AuthToken', data.token)

      window.location.reload()
    }
  }

  const viewLogin = (status) => {
    setError(null)
    setIsLogin(status)
  }

    return (
      <div className="auth-container">
        <div className="auth-container-box">
          <form className="auth-container-form">
            <h2>{ isLogIn ? 'Please log in' : 'Please sign up!' }</h2>
            <input 
              type="email" 
              id="email"
              name="email"
              placeholder="email" 
              onChange={(e) => setEmail(e.target.value)}
            />
            <input 
              type="password" 
              placeholder="password"
              onChange={(e) => setPassword(e.target.value)}
            />
            { !isLogIn && <input 
              type="password" 
              placeholder="confirm password"
              onChange={(e) => setConfirmPassword(e.target.value)}
            />}
          {error && <p>* {error}</p>}
            <input type="submit" className="create" onClick={(e) => handleSubmit(e, isLogIn ? 'login' : 'signup')}/>
          </form>
          <div className="auth-options">
            <button onClick={() => viewLogin(false)} style={{backgroundColor: !isLogIn ? 'rgb(255, 255, 255)' : 'rgb(188, 188, 188)'}}>Sign Up</button>
            <button onClick={() => viewLogin(true)} style={{backgroundColor: isLogIn ? 'rgb(255, 255, 255)' : 'rgb(188, 188, 188)'}}>Login</button>
          </div>
        </div>
      </div>
    )
  }
  
  export default Auth
